CREATE TABLE `bookingsys`.`bookings` ( `id` INT NOT NULL AUTO_INCREMENT , `player1` VARCHAR(100) NOT NULL , `player2` VARCHAR(100) NOT NULL , `timeOfPlay` VARCHAR(100) NOT NULL , `noOfMatches` INT NOT NULL , `bookedBy` VARCHAR(100) NOT NULL , `phoneNum` VARCHAR(100) NOT NULL , `ticketId` VARCHAR(100) NOT NULL , `timeOfBooking` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
