CREATE TABLE `bookingsys`.`orders` ( `id` INT NOT NULL AUTO_INCREMENT , `hplayer` VARCHAR(50) NOT NULL , `aplayer` VARCHAR(50) NOT NULL , `tme` VARCHAR(50) NOT NULL , `matches` INT NOT NULL , `booker` VARCHAR(50) NOT NULL , `phone` VARCHAR(20) NOT NULL , `ordernum` VARCHAR(10) NOT NULL , `approvalstatus` INT NOT NULL DEFAULT '0' , `availability_id` INT NOT NULL , `timeoforder` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;